my code

`
import {OnInit} from '@angular/core';
import {Component, ViewChild} from '@angular/core';
import {PokojeService} from '../pokoje.service';
import {MatPaginator, MatSort, MatTableDataSource} from '@angular/material';

@Component({
  selector: 'table-overview-example',
  templateUrl: './pokoje-list.component.html',
  styleUrls: ['./pokoje-list.component.css']
})
export class PokojeListComponent implements OnInit {
  displayedColumns = ['nazwa', 'typ', 'opis', 'status', 'akcje'];
  dataSource: MatTableDataSource<any>;
  pokoje: any;
  pokojeTypy: any;
  @ViewChild(MatPaginator) paginator: MatPaginator;
  @ViewChild(MatSort) sort: MatSort;

  constructor(private pokojeService: PokojeService) {
  }

  ngOnInit() {
  }

  applyFilter(filterValue: string) {
    this.dataSource.filter = filterValue;
  }

  ngAfterViewInit() {
    let pokojez = [];
    this.pokojeService.pobierzPokoje()
      .subscribe(
        (pokoje: any) => {
          pokoje.forEach(pokoj => {
            if (pokoj[0]['pokojSprzatany'] === 1) {
              pokoj[0]['czystosc'] = 'brudny';
            } else if (pokoj[0]['pokojSprzatany'] === 0) {
              pokoj[0]['czystosc'] = 'sprawdzony';
            }
            else if (pokoj[0]['pokojSprzatany'] === 2) {
              pokoj[0]['czystosc'] = 'czysty';
            }
            let pokojTyp = pokoj[1];
            let obj = pokoj[0];
            obj['pokojTypNazwa'] = pokojTyp;
            pokojez.push(obj);

          });
          this.pokoje = pokojez;
          this.dataSource = new MatTableDataSource(pokojez);
          this.dataSource.paginator = this.paginator;
          this.dataSource.sort = this.sort;
        }
      );

  }
}`

